import React, { useState } from 'react'
import ReactDOM from 'react-dom'


const Header = (props) => {
  console.log(props)
  return(
  <div>
    <h1>{props.title}</h1>
  </div>
)}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.text}
    </button>
)

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const goodClick = newValue => {
    setGood(1 + newValue)
  }

  const badClick = newValue => {
    setBad(newValue + 1)
  }

  const neutralClick = newValue => {
    setNeutral(newValue + 1)
  }

  const feedback = "give feedback"
  const stat = "statistics"
  const button1 = "good"
  const button2 = "netural"
  const button3= "bad"

  return (
    <div>
      <Header title={feedback}/>
      <Button handleClick={() => goodClick(good)} text={button1} />
      <Button handleClick={() => neutralClick(neutral)} text={button2} />
      <Button handleClick={() => badClick(bad)} text={button3} />
      <Header title={stat} />
      <p> {button1} {good} </p>
      <p> {button2} </p>
      <p> {button3} </p>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
